class Utils{static getCanvasSize(){return{width:document.getElementById("drawer").width,height:document.getElementById("drawer").height}}static setCanvasSize(width,height){document.getElementById("drawer").width=width,document.getElementById("drawer").height=height}static get2DPos(v){let{x:x,y:y,z:z}=v,originX=this.getCanvasSize().width/2,originY=this.getCanvasSize().height/2,x2d=originX+x+y*Math.cos(Math.PI/4),y2d=originY+y*Math.sin(Math.PI/4)+z;return new Vector2(x2d,y2d)}static posConvert(v){let{x:x,y:y}=v,canvasSize=this.getCanvasSize(),xCanvas=canvasSize.width/2+x,yCanvas=canvasSize.height/2-y;return new canvasPos(xCanvas,yCanvas)}}